import React, { useState, useEffect, useRef } from 'react';
import MonacoEditor from '@monaco-editor/react';
import './JavaIDE.css';

const JavaIDE = () => {
  const [code, setCode] = useState(`import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        System.out.print("Introduce la cantidad de números a ingresar: ");
        int n = scanner.nextInt();

        int[] numeros = new int[n];

        System.out.println("Introduce los " + n + " números:");
        for (int i = 0; i < n; i++) {
            numeros[i] = scanner.nextInt();
        }

        System.out.println("Números ingresados en orden inverso:");
        for (int i = n - 1; i >= 0; i--) {
            System.out.println(numeros[i]);
        }

        scanner.close();
    }
}`);
  const [input, setInput] = useState('');
  const [output, setOutput] = useState('');
  const [socket, setSocket] = useState(null);
  const [isRunning, setIsRunning] = useState(false);
  const [showInfoModal, setShowInfoModal] = useState(false);
  const [originalCode, setOriginalCode] = useState(code); // Estado para almacenar el código original
  const inputRef = useRef(null);

  useEffect(() => {
    initializeWebSocket(); // Inicializar la conexión WebSocket al cargar el componente
    return closeWebSocket; // Cerrar la conexión WebSocket al desmontar el componente
  }, []);

  const initializeWebSocket = () => {
    const ws = new WebSocket('ws://localhost:5000/run');
    ws.onopen = () => {
      console.log("WebSocket connected");
    };

    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);
      console.log("Received data:", data);
      if (data.output) {
        setOutput((prevOutput) => prevOutput + data.output);
      }
      if (data.error) {
        setOutput((prevOutput) => prevOutput + data.error);
      }
      if (data.status === 'finished') {
        setIsRunning(false);
      }
    };

    ws.onclose = () => {
      console.log("WebSocket disconnected");
      setSocket(null); // Limpiar socket al cerrar conexión
    };

    setSocket(ws);
  };

  const closeWebSocket = () => {
    if (socket) {
      socket.close();
      console.log("WebSocket closed");
    }
  };

  const runCode = () => {
    if (!isRunning && socket) { // Solo ejecutar si no está corriendo y hay un socket
      setOutput(''); // Limpiar la salida
      setIsRunning(true); // Establecer que está corriendo
      console.log("Sending code:", code);
      socket.send(JSON.stringify({ code: code })); // Enviar el código al servidor
    } else {
      initializeWebSocket(); // Si no hay socket o está cerrado, inicializar uno nuevo
    }
  };

  const sendInput = () => {
    if (socket && isRunning) { // Solo enviar si está corriendo y hay un socket
      const userInput = inputRef.current.value;
      console.log("Sending input:", userInput);
      socket.send(userInput); // Enviar la entrada al servidor
      setInput(''); // Limpiar el área de entrada
      inputRef.current.value = ''; // Restablecer el valor del ref
    }
  };

  const stopExecution = () => {
    if (socket && isRunning) { // Solo detener si está corriendo y hay un socket
      socket.close(); // Cerrar la conexión con el servidor
      setIsRunning(false); // Establecer que no está corriendo
      setOutput((prevOutput) => prevOutput + "\n\n--- Ejecución detenida por el usuario ---\n"); // Actualizar la salida
    }
  };

  const toggleInfoModal = () => {
    setShowInfoModal(!showInfoModal);
  };

  return (
    <div className="java-ide">
      <header className="java-ide-header">
        <h1>"Netbeans" en Web</h1>
        <button className="info-button" onClick={toggleInfoModal}>ⅰ</button>
        <button className={`stop-button ${isRunning ? '' : 'hidden'}`} onClick={stopExecution}>
        ◼︎
        </button>
      </header>
      <div className="modal" style={{ display: showInfoModal ? 'block' : 'none' }}>
        <div className="modal-content">
          <span className="close" onClick={toggleInfoModal}>&times;</span>
          <h2>Instrucciones de uso</h2>
          <p>
            - Escribe tu código Java en el editor.<br />
            - Haz clic en el botón "Run" para ejecutar el código.<br />
            - Durante la ejecución, puedes introducir datos en el área de entrada.<br />
            - Utiliza el botón "Enviar" para enviar datos de entrada durante la ejecución.<br />
            - Para detener la ejecución del programa, utiliza el botón "STOP". <br />
            - Recuerda que esto no es un IDE real, ten en cuenta que los archivos sobre los que trabajas son Main.class y Main.java.
          </p>
        </div>
      </div>
      <div className="editor-container">
        <MonacoEditor
          height="50vh"
          language="java"
          value={code}
          onChange={setCode}
        />
      </div>
      <div className="button-container">
        <button className="run-button" onClick={runCode} disabled={isRunning}>
          {isRunning ? 'Running...' : 'Run'}
        </button>
      </div>
      <div className="input-container">
        <textarea
          ref={inputRef}
          className="input-textarea"
          disabled={!isRunning}
          placeholder="Entrada de datos"
          rows="5"
          cols="50"
        />
        <button className="input-button" onClick={sendInput} disabled={!isRunning}>
          Enviar
        </button>
      </div>
      <div className="output-container">
        <pre className="output">{output}</pre>
      </div>
    </div>
  );
}

export default JavaIDE;
