import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useTranslation } from 'react-i18next'; 
import './Inicio.css'; 

const Inicio = () => {
  const { t } = useTranslation();
  const [selectedImage, setSelectedImage] = useState(null);

  const openImage = (imageUrl) => {
    setSelectedImage(imageUrl);
  };

  const closeImage = () => {
    setSelectedImage(null);
  };

  return (
    <div className="inicio-container">
      {/* Modal para ver la imagen en grande */}
      {selectedImage && (
        <div className="modal" onClick={closeImage}>
          <span className="close">&times;</span>
          <img className="modal-content" src={selectedImage} alt="Imagen en grande" />
        </div>
      )}

      {/* Carrusel de im치genes */}
      <div className="owl-wrapper-outer">
        <div className="owl-wrapper" style={{ width: '6050px', left: '0px', display: 'block', transition: '800ms', transform: 'translate3d(-1210px, 0px, 0px)' }}>
          {/* Repetir la estructura de las im치genes */}
          <div className="owl-item" style={{ width: '605px' }}>
            <div className="item">
              <div className="mg-blog-post lg back-img" style={{ backgroundImage: `url('https://www.iesalvarofalomir.es/wp-content/uploads/2023/07/2978507.jpg')` }}>
                <a className="link-div" href="https://www.iesalvarofalomir.es/2024/07/29/rebuda-alumnat-curs-24-25/"> </a>
                <article className="bottom">
                  <span className="post-form"><i className="fas fa-camera"></i></span>
                  <div className="mg-blog-category">
                    <a className="newsup-categories category-color-1" href="https://www.iesalvarofalomir.es/category/informacio/" alt="Ver todas las entradas en Informaci칩">Informaci칩</a>
                  </div>
                  <h4 className="title">
                    <a href="https://www.iesalvarofalomir.es/2024/07/29/rebuda-alumnat-curs-24-25/">REBUDA ALUMNAT CURS 24-25</a>
                  </h4>
                  <div className="mg-blog-meta">
                    <span className="mg-blog-date">
                      <i className="fas fa-clock"></i>
                      <a href="https://www.iesalvarofalomir.es/2024/07/">29/07/2024</a>
                    </span>
                  </div>
                </article>
              </div>
            </div>
          </div>
          {/* Contin칰a a침adiendo m치s "owl-item" como en tu c칩digo original */}
        </div>
      </div>
    </div>
  );
};

export default Inicio;
