import React, { useState } from "react";
import initSqlJs from 'sql.js';

const SQLiteManager = () => {
  const [db, setDb] = useState(null);
  const [query, setQuery] = useState("");
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  const [dbInitialized, setDbInitialized] = useState(false);

  // Inicializar la base de datos SQLite
  const initDb = async () => {
    try {
      // Importar sql.js y localizar el archivo wasm
      const SQL = await initSqlJs({
        locateFile: file => `${process.env.PUBLIC_URL}/sql-wasm.wasm` // O usar la URL del CDN
      });
      const database = new SQL.Database();
      setDb(database);
      setDbInitialized(true);
      console.log("Base de datos SQLite inicializada.");
    } catch (e) {
      console.error("Error al inicializar SQLite:", e);
      setError("Error al inicializar SQLite: " + e.message);
    }
  };

  // Ejecutar consulta SQL
  const executeQuery = () => {
    if (db) {
      try {
        const res = db.exec(query);
        setResult(res);
        setError(null);
      } catch (e) {
        setError(e.message);
        setResult(null);
      }
    }
  };

  // Renderizar los resultados de la consulta
  const renderResult = () => {
    if (!result || result.length === 0) return null;

    return (
      <table style={{ width: '100%', border: '1px solid black', marginTop: '20px' }}>
        <thead>
          <tr>
            {result[0].columns.map((col, index) => (
              <th key={index} style={{ border: '1px solid black', padding: '8px' }}>{col}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {result[0].values.map((row, rowIndex) => (
            <tr key={rowIndex}>
              {row.map((val, valIndex) => (
                <td key={valIndex} style={{ border: '1px solid black', padding: '8px' }}>{val}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    );
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h2>Gestor de SQLite en React</h2>

      {/* Botón para inicializar la base de datos */}
      <button 
        onClick={initDb} 
        style={{
          padding: '10px 20px',
          backgroundColor: dbInitialized ? 'green' : '#007BFF',
          color: 'white',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
          marginBottom: '20px',
        }}
        disabled={dbInitialized}
      >
        {dbInitialized ? 'Base de Datos Inicializada' : 'Inicializar Base de Datos'}
      </button>

      {/* Mostrar el cuadro de texto solo si la base de datos está inicializada */}
      {dbInitialized && (
        <>
          <div>
            <textarea
              rows="5"
              cols="50"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder="Escribe tu consulta SQL aquí..."
              style={{
                width: '100%',
                padding: '10px',
                marginBottom: '10px',
                borderRadius: '5px',
                border: '1px solid #ccc',
                fontFamily: 'monospace',
                fontSize: '16px',
              }}
            />
            <br />
            <button
              onClick={executeQuery}
              style={{
                padding: '10px 20px',
                backgroundColor: '#28a745',
                color: 'white',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer',
              }}
            >
              Ejecutar Consulta
            </button>
          </div>
        </>
      )}

      {/* Mostrar error si hay alguno */}
      {error && <p style={{ color: 'red', marginTop: '20px' }}>Error: {error}</p>}

      {/* Mostrar los resultados de la consulta */}
      <div>{renderResult()}</div>
    </div>
  );
};

export default SQLiteManager;
