import React, { useState } from 'react';
import MonacoEditor from '@monaco-editor/react';
import axios from 'axios';

function App() {
  const [code, setCode] = useState(`import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        System.out.print("Introduce la cantidad de números a ingresar: ");
        int n = scanner.nextInt();

        int[] numeros = new int[n];

        System.out.println("Introduce los " + n + " números:");
        for (int i = 0; i < n; i++) {
            numeros[i] = scanner.nextInt();
        }

        System.out.println("Números ingresados en orden inverso:");
        for (int i = n - 1; i >= 0; i--) {
            System.out.println(numeros[i]);
        }

        scanner.close();
    }
}`);
  const [input, setInput] = useState('');
  const [output, setOutput] = useState('');

  const runCode = async () => {
    try {
      const response = await axios.post('/run', { code, input });
      setOutput(response.data);
    } catch (error) {
      setOutput(error.response ? error.response.data : error.message);
    }
  };

  return (
    <div className="App">
      <MonacoEditor
        height="70vh"
        language="java"
        value={code}
        onChange={setCode}
      />
      <textarea
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder="Introduce el valor de entrada"
        rows="10"
        cols="50"
      />
      <button onClick={runCode}>Run</button>
      <pre>{output}</pre>
    </div>
  );
}

export default App;
