import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import './Inicio.css'; 

const Seccion = ({ imageUrl, title, description, linkTo }) => {
  const { t } = useTranslation();
  const [selectedImage, setSelectedImage] = useState(null);

  const openImage = () => setSelectedImage(imageUrl);
  const closeImage = () => setSelectedImage(null);

  return (
    <div className="seccion">
      {selectedImage && (
        <div className="modal" onClick={closeImage}>
          <span className="close">&times;</span>
          <img className="modal-content" src={selectedImage} alt={t(title)} />
        </div>
      )}
      <div className="imagen-seccion" onClick={openImage} style={{ cursor: 'pointer' }}>
        <img src={imageUrl} alt={t(title)} />
      </div>
      <div className="texto-seccion">
        <h2>{t(title)}</h2>
        <p>{t(description)}</p>
        <Link to={linkTo} className="enlace">{t('Ver más')}</Link>
      </div>
    </div>
  );
};

const Inicio = () => {
  return (
    <div className="inicio-container">
      <Seccion
        imageUrl="https://castellonaldia.elmundo.es/binrepository/697x420/59c0/580d420/none/291842500/ETPM/imagen-barracones-ies-alvaro-falomir_381-3025841_20231024171127.jpg"
        title="Cursos de Formación Profesional"
        description="Descubre nuestra oferta educativa..."
        linkTo="/cursos-fp"
      />
      <Seccion
        imageUrl="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS-ldGxqjlfhrBqt6UBTZ2gZy5aNq09ah8TNQ&s"
        title="Eventos y Actividades"
        description="Mantente al día con nuestros eventos..."
        linkTo="/eventos"
      />
      <Seccion
        imageUrl="/imagenes/herramientas.webp"
        title="Herramientas y Recursos"
        description="Aquí se encuentran las herramientas de programación"
        linkTo="/tools"
      />
    </div>
  );
};

export default Inicio;
