import React, { useState } from 'react';
import MonacoEditor from '@monaco-editor/react';
import axios from 'axios';

function App() {
  const [code, setCode] = useState(`import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        System.out.print("Introduce un número: ");
        int numero = scanner.nextInt();

        int resultado = numero * 2;
        System.out.println("El número introducido multiplicado por 2 es: " + resultado);

        scanner.close();
    }
}`);
  const [input, setInput] = useState('');
  const [output, setOutput] = useState('');

  const runCode = async () => {
    try {
      const response = await axios.post('/run', { code, input });
      setOutput(response.data);
    } catch (error) {
      setOutput(error.response ? error.response.data : error.message);
    }
  };

  return (
    <div className="App">
      <MonacoEditor
        height="70vh"
        language="java"
        value={code}
        onChange={setCode}
      />
      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder="Introduce el valor de entrada"
      />
      <button onClick={runCode}>Run</button>
      <pre>{output}</pre>
    </div>
  );
}

export default App;
