import React, { useState } from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom'; // Importamos Link
import { useTranslation } from 'react-i18next';
import './i18n'; // Importa la configuración de i18n
import JavaIDE from './components/JavaIDE';
import Inicio from './components/Inicio';
import Titulo from './components/Titulo';
import Herramientas from './components/Herramientas';

const App = () => {
  const { i18n } = useTranslation();
  const [language, setLanguage] = useState('es');

  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
    setLanguage(lng);
  };

  return (
    <BrowserRouter>
      <div className="app-container">
        <Titulo />
        <div className="flags-container">
          <img
            src="/flags/es.png"
            alt="Español"
            onClick={() => changeLanguage('es')}
            style={{ width: '32px', cursor: 'pointer', marginRight: '10px' }}
          />
          <img
            src="/flags/cat.jpg"
            alt="Valencià"
            onClick={() => changeLanguage('ca')}
            style={{ width: '32px', cursor: 'pointer' }}
          />
        </div>

        {/* Icono de casa para volver al inicio */}
        <div className="home-icon">
          <Link to="/">
            <img
              src="/icons/home.png" // Aquí pones la ruta a tu icono de casa
              alt="Inicio"
              style={{ width: '32px', cursor: 'pointer', margin: '10px' }}
            />
          </Link>
        </div>

        <Routes>
          <Route path="/tools" element={<Herramientas />} />
          <Route path="/javaide" element={<JavaIDE />} />
          <Route path="/" element={<Inicio />} />
        </Routes>
      </div>
    </BrowserRouter>
  );
};

export default App;
