import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useTranslation } from 'react-i18next'; 
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Pagination, Autoplay } from 'swiper';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import './Inicio.css'; 

const Inicio = () => {
  const { t } = useTranslation();
  const [selectedImage, setSelectedImage] = useState(null);

  const openImage = (imageUrl) => {
    setSelectedImage(imageUrl);
  };

  const closeImage = () => {
    setSelectedImage(null);
  };

  // Datos del carrusel
  const carouselItems = [
    {
      imageUrl: 'https://www.iesalvarofalomir.es/wp-content/uploads/2023/07/2978507.jpg',
      link: 'https://www.iesalvarofalomir.es/2024/07/29/rebuda-alumnat-curs-24-25/',
      categories: [
        { name: 'Informació', url: 'https://www.iesalvarofalomir.es/category/informacio/' }
      ],
      title: 'REBUDA ALUMNAT CURS 24-25',
      date: '29/07/2024',
      dateLink: 'https://www.iesalvarofalomir.es/2024/07/',
    },
    {
      imageUrl: 'https://www.iesalvarofalomir.es/wp-content/uploads/2024/07/IMG-20240630-WA0005.jpg',
      link: 'https://www.iesalvarofalomir.es/2024/07/01/nou-curs-despecialitzacio-administracio-al-nuvol/',
      categories: [
        { name: 'Cicles', url: 'https://www.iesalvarofalomir.es/category/cicles/' }
      ],
      title: 'NOU CURS D’ESPECIALITZACIÓ: ADMINISTRACIÓ AL NÚVOL',
      date: '01/07/2024',
      dateLink: 'https://www.iesalvarofalomir.es/2024/07/',
    },
    {
      imageUrl: 'https://www.iesalvarofalomir.es/wp-content/uploads/2023/07/florian-olivo-Mf23RF8xArY-unsplash-scaled.jpg',
      link: 'https://www.iesalvarofalomir.es/2024/07/01/oferta-docent-especialista-videojocs-i-realitat-virtual/',
      categories: [
        { name: 'Cicles', url: 'https://www.iesalvarofalomir.es/category/cicles/' },
        { name: 'Institut', url: 'https://www.iesalvarofalomir.es/category/institut/' }
      ],
      title: 'OFERTA DOCENT PROFESSORAT EXPERT EN VIDEOJOCS O ADMINISTRACIÓ AL NÚVOL',
      date: '01/07/2024',
      dateLink: 'https://www.iesalvarofalomir.es/2024/07/',
    },
    {
      imageUrl: 'https://www.iesalvarofalomir.es/wp-content/uploads/2024/06/graduac-4t-eso.jpeg',
      link: 'https://www.iesalvarofalomir.es/2024/06/15/acte-de-comiat-de-lalumnat-de-4t-deso/',
      categories: [
        { name: 'ESO', url: 'https://www.iesalvarofalomir.es/category/eso/' }
      ],
      title: 'ACTE DE COMIAT DE L’ALUMNAT DE 4t D’ESO',
      date: '15/06/2024',
      dateLink: 'https://www.iesalvarofalomir.es/2024/06/',
    },
    {
      imageUrl: 'https://www.iesalvarofalomir.es/wp-content/uploads/2024/06/FB93A2C4-9A19-4F19-B45B-071C4E688D2E-scaled.jpeg',
      link: 'https://www.iesalvarofalomir.es/2024/06/12/robotica-al-cardenal-cisneros/',
      categories: [
        { name: 'ESO', url: 'https://www.iesalvarofalomir.es/category/eso/' }
      ],
      title: 'ROBÒTICA AL CARDENAL CISNEROS',
      date: '12/06/2024',
      dateLink: 'https://www.iesalvarofalomir.es/2024/06/',
    },
  ];

  return (
    <div className="inicio-container">
      {/* Modal para ver la imagen en grande */}
      {selectedImage && (
        <div className="modal" onClick={closeImage}>
          <span className="close">&times;</span>
          <img className="modal-content" src={selectedImage} alt="Imagen en grande" />
        </div>
      )}

      {/* Carrusel de imágenes usando Swiper */}
      <Swiper
        modules={[Navigation, Pagination, Autoplay]}
        spaceBetween={10}
        slidesPerView={1}
        navigation
        pagination={{ clickable: true }}
        autoplay={{ delay: 5000, disableOnInteraction: false }}
        loop
        className="swiper-container"
      >
        {carouselItems.map((item, index) => (
          <SwiperSlide key={index}>
            <div 
              className="mg-blog-post lg back-img" 
              style={{ backgroundImage: `url('${item.imageUrl}')` }}
              onClick={() => openImage(item.imageUrl)}
              style={{ cursor: 'pointer', backgroundSize: 'cover', backgroundPosition: 'center', height: '400px', position: 'relative' }}
            >
              <a className="link-div" href={item.link}> </a>
              <article className="bottom">
                <span className="post-form"><i className="fas fa-camera"></i></span>
                <div className="mg-blog-category">
                  {item.categories.map((cat, idx) => (
                    <a 
                      key={idx} 
                      className="newsup-categories category-color-1" 
                      href={cat.url} 
                      alt={`Ver todas las entradas en ${cat.name}`}
                      style={{ marginRight: '5px', color: 'white', fontSize: '12px', textTransform: 'uppercase' }}
                    >
                      {cat.name}
                    </a>
                  ))}
                </div>
                <h4 className="title">
                  <a href={item.link} style={{ color: 'white', textDecoration: 'none' }}>
                    {item.title}
                  </a>
                </h4>
                <div className="mg-blog-meta">
                  <span className="mg-blog-date">
                    <i className="fas fa-clock"></i>
                    <a href={item.dateLink} style={{ color: 'white', textDecoration: 'none' }}>
                      {item.date}
                    </a>
                  </span>
                </div>
              </article>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>

      {/* Otras secciones existentes */}
      {/* Sección de Cursos de FP */}
      <section className="seccion">
        <div className="imagen-seccion">
          <img 
            src="https://castellonaldia.elmundo.es/binrepository/697x420/59c0/580d420/none/291842500/ETPM/imagen-barracones-ies-alvaro-falomir_381-3025841_20231024171127.jpg" 
            alt={t('Cursos de Formación Profesional')}
            onClick={() => openImage('https://castellonaldia.elmundo.es/binrepository/697x420/59c0/580d420/none/291842500/ETPM/imagen-barracones-ies-alvaro-falomir_381-3025841_20231024171127.jpg')}
            style={{ cursor: 'pointer' }}
          />
        </div>
        <div className="texto-seccion">
          <h2>{t('Cursos de Formación Profesional')}</h2>
          <p>{t('Descubre nuestra oferta educativa...')}</p>
          <Link to="/cursos-fp" className="enlace">{t('Ver más')}</Link>
        </div>
      </section>

      {/* Sección de Eventos */}
      <section className="seccion">
        <div className="imagen-seccion">
          <img 
            src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS-ldGxqjlfhrBqt6UBTZ2gZy5aNq09ah8TNQ&s" 
            alt={t('Eventos y Actividades')}
            onClick={() => openImage('https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS-ldGxqjlfhrBqt6UBTZ2gZy5aNq09ah8TNQ&s')}
            style={{ cursor: 'pointer' }}
          />
        </div>
        <div className="texto-seccion">
          <h2>{t('Eventos y Actividades')}</h2>
          <p>{t('Mantente al día con nuestros eventos...')}</p>
          <Link to="/eventos" className="enlace">{t('Ver más')}</Link>
        </div>
      </section>

      {/* Otras secciones */}
      <section className="seccion seccion-invertida">
        <div className="imagen-seccion">
          <img 
            src="/imagenes/herramientas.webp" 
            alt={t('Herramientas y Recursos')}
            onClick={() => openImage('/imagenes/herramientas.webp')}
            style={{ cursor: 'pointer' }}
          />
        </div>
        <div className="texto-seccion">
          <h2>{t('Herramientas y Recursos')}</h2> 
          <p>
            {t('Aqui se encuentran las herramientas de programación')}
          </p>
